Consider the lower triangular $m \times m$ matrix $L$, stored in CSR storage format with unit diagonal. The following example solves $L \cdot y = x$. //CreaterocSPARSEhandle
rocsparse_handlehandle;
rocsparse_create_handle(&handle);

//Creatematrixdescriptor
rocsparse_mat_descrdescr;
rocsparse_create_mat_descr(&descr);
rocsparse_set_mat_fill_mode(descr,rocsparse_fill_mode_lower);
rocsparse_set_mat_diag_type(descr,rocsparse_diag_type_unit);

//Creatematrixinfostructure
rocsparse_mat_infoinfo;
rocsparse_create_mat_info(&info);

//Obtainrequiredbuffersize
size_tbuffer_size;
rocsparse_dcsrsv_buffer_size(handle,
rocsparse_operation_none,
m,
nnz,
descr,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
&buffer_size);

//Allocatetemporarybuffer
void*temp_buffer;
hipMalloc(&temp_buffer,buffer_size);

//Performanalysisstep
rocsparse_dcsrsv_analysis(handle,
rocsparse_operation_none,
m,
nnz,
descr,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
rocsparse_analysis_policy_reuse,
rocsparse_solve_policy_auto,
temp_buffer);

//SolveLy=x
rocsparse_dcsrsv_solve(handle,
rocsparse_operation_none,
m,
nnz,
&alpha,
descr,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
x,
y,
rocsparse_solve_policy_auto,
temp_buffer);

//Nozeropivotshouldbefound,withLhavingunitdiagonal

//Cleanup
hipFree(temp_buffer);
rocsparse_destroy_mat_info(info);
rocsparse_destroy_mat_descr(descr);
rocsparse_destroy_handle(handle);

This example multiplies a CSR matrix with a dense matrix. //12030
//A=04500
//60078

rocsparse_intm=3;
rocsparse_intk=5;
rocsparse_intnnz=8;

csr_row_ptr[m+1]={0,3,5,8};//devicememory
csr_col_ind[nnz]={0,1,3,1,2,0,3,4};//devicememory
csr_val[nnz]={1,2,3,4,5,6,7,8};//devicememory

//SetdimensionnofB
rocsparse_intn=64;

//AllocateandgeneratedensematrixB
std::vector<float>hB(k*n);
for(rocsparse_inti=0;i<k*n;++i)
{
hB[i]=static_cast<float>(rand())/RAND_MAX;
}

//CopyBtothedevice
float*B;
hipMalloc((void**)&B,sizeof(float)*k*n);
hipMemcpy(B,hB.data(),sizeof(float)*k*n,hipMemcpyHostToDevice);

//alphaandbeta
floatalpha=1.0f;
floatbeta=0.0f;

//AllocatememoryfortheresultingmatrixC
float*C;
hipMalloc((void**)&C,sizeof(float)*m*n);

//Performthematrixmultiplication
rocsparse_scsrmm(handle,
rocsparse_operation_none,
rocsparse_operation_none,
m,
n,
k,
nnz,
&alpha,
descr,
csr_val,
csr_row_ptr,
csr_col_ind,
B,
k,
&beta,
C,
m);

Consider the sparse $m \times m$ matrix $A$, stored in CSR storage format. The following example computes the incomplete LU factorization $M \approx LU$ and solves the preconditioned system $My = x$. //CreaterocSPARSEhandle
rocsparse_handlehandle;
rocsparse_create_handle(&handle);

//CreatematrixdescriptorforM
rocsparse_mat_descrdescr_M;
rocsparse_create_mat_descr(&descr_M);

//CreatematrixdescriptorforL
rocsparse_mat_descrdescr_L;
rocsparse_create_mat_descr(&descr_L);
rocsparse_set_mat_fill_mode(descr_L,rocsparse_fill_mode_lower);
rocsparse_set_mat_diag_type(descr_L,rocsparse_diag_type_unit);

//CreatematrixdescriptorforU
rocsparse_mat_descrdescr_U;
rocsparse_create_mat_descr(&descr_U);
rocsparse_set_mat_fill_mode(descr_U,rocsparse_fill_mode_upper);
rocsparse_set_mat_diag_type(descr_U,rocsparse_diag_type_non_unit);

//Creatematrixinfostructure
rocsparse_mat_infoinfo;
rocsparse_create_mat_info(&info);

//Obtainrequiredbuffersize
size_tbuffer_size_M;
size_tbuffer_size_L;
size_tbuffer_size_U;
rocsparse_dcsrilu0_buffer_size(handle,
m,
nnz,
descr_M,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
&buffer_size_M);
rocsparse_dcsrsv_buffer_size(handle,
rocsparse_operation_none,
m,
nnz,
descr_L,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
&buffer_size_L);
rocsparse_dcsrsv_buffer_size(handle,
rocsparse_operation_none,
m,
nnz,
descr_U,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
&buffer_size_U);

size_tbuffer_size=max(buffer_size_M,max(buffer_size_L,buffer_size_U));

//Allocatetemporarybuffer
void*temp_buffer;
hipMalloc(&temp_buffer,buffer_size);

//Performanalysissteps,usingrocsparse_analysis_policy_reusetoimprove
//computationperformance
rocsparse_dcsrilu0_analysis(handle,
m,
nnz,
descr_M,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
rocsparse_analysis_policy_reuse,
rocsparse_solve_policy_auto,
temp_buffer);
rocsparse_dcsrsv_analysis(handle,
rocsparse_operation_none,
m,
nnz,
descr_L,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
rocsparse_analysis_policy_reuse,
rocsparse_solve_policy_auto,
temp_buffer);
rocsparse_dcsrsv_analysis(handle,
rocsparse_operation_none,
m,
nnz,
descr_U,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
rocsparse_analysis_policy_reuse,
rocsparse_solve_policy_auto,
temp_buffer);

//Checkforzeropivot
rocsparse_intposition;
if(rocsparse_status_zero_pivot==rocsparse_csrilu0_zero_pivot(handle,
info,
&position))
{
printf("AhasstructuralzeroatA(%d,%d)\n",position,position);
}

//ComputeincompleteLUfactorization
rocsparse_dcsrilu0(handle,
m,
nnz,
descr_M,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
rocsparse_solve_policy_auto,
temp_buffer);

//Checkforzeropivot
if(rocsparse_status_zero_pivot==rocsparse_csrilu0_zero_pivot(handle,
info,
&position))
{
printf("Uhasstructuraland/ornumericalzeroatU(%d,%d)\n",
position,
position);
}

//SolveLz=x
rocsparse_dcsrsv_solve(handle,
rocsparse_operation_none,
m,
nnz,
&alpha,
descr_L,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
x,
z,
rocsparse_solve_policy_auto,
temp_buffer);

//SolveUy=z
rocsparse_dcsrsv_solve(handle,
rocsparse_operation_none,
m,
nnz,
&alpha,
descr_U,
csr_val,
csr_row_ptr,
csr_col_ind,
info,
z,
y,
rocsparse_solve_policy_auto,
temp_buffer);

//Cleanup
hipFree(temp_buffer);
rocsparse_destroy_mat_info(info);
rocsparse_destroy_mat_descr(descr_M);
rocsparse_destroy_mat_descr(descr_L);
rocsparse_destroy_mat_descr(descr_U);
rocsparse_destroy_handle(handle);

