The following example sorts a $3 \times 3$ CSR matrix. //123
//A=456
//789
rocsparse_intm=3;
rocsparse_intn=3;
rocsparse_intnnz=9;

csr_row_ptr[m+1]={0,3,6,9};//devicememory
csr_col_ind[nnz]={2,0,1,0,1,2,0,2,1};//devicememory
csr_val[nnz]={3,1,2,4,5,6,7,9,8};//devicememory

//Createpermutationvectorpermastheidentitymap
rocsparse_int*perm;
hipMalloc((void**)&perm,sizeof(rocsparse_int)*nnz);
rocsparse_create_identity_permutation(handle,nnz,perm);

//Allocatetemporarybuffer
size_tbuffer_size;
void*temp_buffer;
rocsparse_csrsort_buffer_size(handle,m,n,nnz,csr_row_ptr,csr_col_ind,&buffer_size);
hipMalloc(&temp_buffer,buffer_size);

//SorttheCSRmatrix
rocsparse_csrsort(handle,m,n,nnz,descr,csr_row_ptr,csr_col_ind,perm,temp_buffer);

//Gathersortedcsr_valarray
float*csr_val_sorted;
hipMalloc((void**)&csr_val_sorted,sizeof(float)*nnz);
rocsparse_sgthr(handle,nnz,csr_val,csr_val_sorted,perm,rocsparse_index_base_zero);

//Cleanup
hipFree(temp_buffer);
hipFree(perm);
hipFree(csr_val);

